import { Circle, Popup, Tooltip } from "react-leaflet";
import { numeral } from "numeral";

export const displayCountryNum = (data, casesType = "cases") => {
  const formatedNum = (num) => {
    new Intl.NumberFormat("en").format(num);
  };

  return data.map((country, index) => {
    const dateUpdate = new Date(country.updated).toLocaleString();

    const html = `
    <span class="icon-marker">
      <span class="icon-marker-tooltip">
        <h2>${country.country}</h2>
        <ul>
          <li><strong>Confirmed:</strong> ${formatedNum(country.cases)}</li>
          <li><strong>Deaths:</strong> ${formatedNum(country.deaths)}</li>
          <li><strong>Recovered:</strong> ${formatedNum(country.recovered)}</li>
          <li><strong>Last Update:</strong> ${dateUpdate}</li>
        </ul>
      </span>
      New cases : ${formatedNum(country.todayCases)}
    </span>
  `;

    return (
      <Circle
        key={index}
        center={[country.countryInfo.lat, country.countryInfo.long]}
        radius={Math.floor((country.cases * 10) / 100)}
        color={"red"}
        fillColor={"red"}
      >
        <Tooltip>
          <div>
            <span>
              <h2>{country.country}</h2>
              <ul>
                <li>
                  <strong>Confirmed:</strong> {formatedNum(country.cases)}
                </li>
                <li>
                  <strong>Deaths:</strong> {formatedNum(country.deaths)}
                </li>
                <li>
                  <strong>Recovered:</strong> {formatedNum(country.recovered)}
                </li>
                <li>
                  <strong>Last Update:</strong> {dateUpdate}
                </li>
              </ul>
            </span>
            New cases :{formatedNum(country.todayCases)}
          </div>
        </Tooltip>
      </Circle>
    );
  });
};
