import React, { useContext, useState } from "react";
import styled from "styled-components";
import { daysCount } from "../helpers/userPageHelper";
import { WorldDataContext } from "./WorldDataContext";

const Country = () => {
  const [countryData, setCountryData] = useState("");
  const [countryChoice, setCountryChoice] = useState("Worldwide");
  const { data, isLoading, country, getSpecificCountry } = useContext(
    WorldDataContext
  );
  const [covNum, setCovNum] = useState([{ cases: 0, recovered: 0, deaths: 0 }]);

  const getCountryInfo = async (specificCountry) => {
    const numDays = daysCount();
    console.log(numDays);
    let url;
    if (countryChoice === "Worldwide") {
      url = `/api/historical/all/${numDays}`;
    } else {
      url = `/api/${specificCountry}/${numDays}`;
    }

    const response = await fetch(url);
    const result = await response.json();
    console.log(result.data);
    setCountryData(result.data);
    setCountryChoice(result);
    return result;
  };

  React.useEffect(() => {
    getCountryInfo(country);
  }, [country]);

  // let cases = countryData && Object.entries(countryData?.timeline?.cases);
  console.log({ countryData });
  console.log(countryChoice);
  return (
    <Wrapper>
      <div>Choose a country</div>
      <select
        value={country}
        // {!countryData ? "Choose a Country" : countryData?.country}
        onChange={(e) => {
          getSpecificCountry(e.target.value);
        }}
      >
        <option value={country}>{country ? country : ""}</option>
        {isLoading ? (
          <></>
        ) : (
          data?.map((item, index) => {
            return (
              <option key={index} value={item.country}>
                {item.country}
              </option>
            );
          })
        )}
      </select>
      {countryData === undefined ? (
        <></>
      ) : (
        <>
          <div>Historical data</div>

          <>
            {/* {countryData?.timeline ? ( .map((item) => {
              return (
                <div key={item[0]}>
                  {item[0]} - {item[1]}
                </div>
              );
            })} */}
          </>
        </>
      )}
    </Wrapper>
  );
};

const Wrapper = styled.div``;

export default Country;
