import React, { useContext, useState } from "react";

import {
  AreaChart,
  Area,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
} from "recharts";
import { WorldDataContext } from "./WorldDataContext";
import { daysCount } from "../helpers/userPageHelper";
const data = [
  { name: "Page A", uv: 4000, pv: 2400, amt: 2400 },
  { name: "Page B", uv: 3000, pv: 1398, amt: 2210 },
  { name: "Page C", uv: 2000, pv: 9800, amt: 2290 },
  { name: "Page D", uv: 2780, pv: 3908, amt: 2000 },
  { name: "Page E", uv: 1890, pv: 4800, amt: 2181 },
  { name: "Page F", uv: 2390, pv: 3800, amt: 2500 },
  { name: "Page G", uv: 3490, pv: 4300, amt: 2100 },
];

const UserChart = () => {
  const { countryData } = useContext(WorldDataContext);
  const [numCases, setCases] = useState(null);
  const [numDeaths, setDeaths] = useState(null);
  const [numRecovered, setRecovered] = useState(null);
  // console.log({ countryData });
  const cases = countryData && Object.values(countryData.cases);
  const deaths = countryData && Object.values(countryData.deaths);
  const recovered = countryData && Object.values(countryData.recovered);

  const merged = [...cases, ...deaths, ...recovered];
  // const merged = (arr1, arr2) => {
  //   return arr1.map((item, i) => {
  //     if (item[0] === arr2[i][0]) {
  //       return Object.assign({}, item, arr2[i]);
  //     }
  //   });
  // };
  const numdays = daysCount();
  console.log({ cases });
  console.log({ merged });
  console.log("CASES: ", merged.slice(0, numdays));
  console.log("DEATHS: ", merged.slice(numdays, numdays * 2));
  console.log("RECOVERED: ", merged.slice(numdays * 2, merged.length));

  return (
    <div>
      <AreaChart
        width={600}
        height={400}
        data={data}
        margin={{ top: 10, right: 30, left: 0, bottom: 0 }}
      >
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="name" />
        <YAxis />
        <Tooltip />
        <Area
          type="monotone"
          dataKey="uv"
          stackId="1"
          stroke="#8884d8"
          fill="#8884d8"
        />
        <Area
          type="monotone"
          dataKey="pv"
          stackId="1"
          stroke="#82ca9d"
          fill="#82ca9d"
        />
        <Area
          type="monotone"
          dataKey="amt"
          stackId="1"
          stroke="#ffc658"
          fill="#ffc658"
        />
      </AreaChart>{" "}
    </div>
  );
};

export default UserChart;
