import { Circle, Popup, Tooltip } from "react-leaflet";
import { numeral } from "numeral";

export const displayCountryNum = (data, casesType = "cases") => {
  return data.map((country, index) => {
    const dateUpdate = new Date(country.updated).toLocaleString();

    const html = `
    <span class="icon-marker">
      <span class="icon-marker-tooltip">
        <h2>${country.country}</h2>
        <ul>
          <li><strong>Confirmed:</strong> ${country.cases}</li>
          <li><strong>Deaths:</strong> ${country.deaths}</li>
          <li><strong>Recovered:</strong> ${country.recovered}</li>
          <li><strong>Last Update:</strong> ${dateUpdate}</li>
        </ul>
      </span>
      New cases : ${country.todayCases}
    </span>
  `;
    const numCases = new Intl.NumberFormat("en").format(country.cases);

    return (
      <Circle
        key={index}
        center={[country.countryInfo.lat, country.countryInfo.long]}
        radius={Math.floor((country.cases * 10) / 100)}
        color={"red"}
        fillColor={"red"}
      >
        <Tooltip>{html}</Tooltip>
      </Circle>
    );
  });
};
