import React, { useContext } from "react";
import styled from "styled-components";
import { WorldDataContext } from "./WorldDataContext";
import WorldMap from "./WorldMap";
import Select from "@material-ui/core/Select";
import MenuItem from "@material-ui/core/MenuItem";
import { FormControl, InputLabel } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));

const HomePage = () => {
  const classes = useStyles();
  const {
    data,
    country,
    isLoading,
    getSpecificCountry,
    coordinates,
    zoomScale,
  } = useContext(WorldDataContext);

  // const [data, setData] = React.useState();
  // const [country, setCountry] = React.useState();
  // const [isLoading, SetIsLoading] = React.useState(true);

  // React.useEffect(() => {
  //   const getCountriesData = async () => {
  //     let response = await fetch("/api/home");

  //     let data = await response.json();
  //     return data;
  //   };

  //   getCountriesData().then((countriesData) => {
  //     setData(countriesData.allCountriesData);
  //     console.log("DATA: ", countriesData.allCountriesData);
  //     SetIsLoading(false);
  //   });
  // }, [country]);

  // const getSpecificCountry = (specificCountry) => {
  //   const chosenCountry = data?.find((item) => {
  //     return item.country.toLowerCase() === specificCountry.toLowerCase();
  //   });

  //   setCountry(chosenCountry);
  //   return chosenCountry;
  // };

  return (
    <Wrapper>
      World Dashboard
      <div>
        <FormControl className={classes.formControl}>
          <InputLabel id="demo-simple-select-label">Country</InputLabel>
          <Select
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value="Choose a country"
            onChange={(e) => {
              getSpecificCountry(e.target.value);
            }}
          >
            <MenuItem>Choose a country</MenuItem>
            {isLoading ? (
              <></>
            ) : (
              data?.map((item, index) => {
                return (
                  <MenuItem key={index} value={item.country}>
                    {item.country}
                  </MenuItem>
                );
              })
            )}
          </Select>
        </FormControl>

        {/* <select
          onChange={(e) => {
            getSpecificCountry(e.target.value);
          }}
        >
          <option>Choose a country</option>
          {isLoading ? (
            <></>
          ) : (
            data?.map((item, index) => {
              return (
                <option key={index} value={item.country}>
                  {item.country}
                </option>
              );
            })
          )}
        </select> */}
      </div>
      {country ? (
        <CountryWrapper>
          <ImgWrapper>
            <Flag src={country?.countryInfo.flag} />
          </ImgWrapper>
          <CountryStats>
            <div>population: {country?.population}</div>
            <div>Total cases : {country?.cases}</div>
            <div>Total Deaths : {country?.deaths}</div>
            <div>Total Recovered : {country?.recovered}</div>
          </CountryStats>
        </CountryWrapper>
      ) : (
        <>👆🏼 Select a country 👆🏼 </>
      )}
      <WorldContainer>
        <WorldMap
          data={data}
          country={country}
          coordinates={coordinates}
          zoomScale={zoomScale}
        />
      </WorldContainer>
    </Wrapper>
  );
};

const Wrapper = styled.div``;
const ImgWrapper = styled.div``;
const Flag = styled.img`
  border-radius: 50%;
  height: 50px;
  width: 50px;
  border: solid 1px white;
  /* object-fit: contain; */
`;

const CountryWrapper = styled.div`
  display: flex;
  flex-direction: row;
`;

const CountryStats = styled.div``;

const WorldContainer = styled.div`
  margin: 0 auto;
  width: 100vw;
  height: 80vh;
`;
export default HomePage;
