import React, { useContext, useState } from "react";
import styled from "styled-components";
import { daysCount } from "../helpers/userPageHelper";
import { WorldDataContext } from "./WorldDataContext";

const Country = () => {
  const [countryData, setCountryData] = useState();
  const [countryChoice, setCountryChoice] = useState("Worldwide");
  const { data, isLoading } = useContext(WorldDataContext);

  const getCountryInfo = async (specificCountry) => {
    const numDays = daysCount();
    const url =
      countryChoice === "Worldwide"
        ? `/api/historical/all`
        : `/api/${specificCountry}/${numDays}`;
    const response = await fetch(url);
    const result = await response.json();
    setCountryData(result.countryInfo);
    setCountryChoice(result.countryInfo.country);
    return result.countryInfo;
  };

  React.useEffect(() => {
    getCountryInfo();
  }, [countryData, countryChoice]);

  const cases = countryData && Object.entries(countryData?.timeline?.cases);
  console.log({ countryData });
  return (
    <Wrapper>
      <div>Choose a country</div>
      <select
        value={countryChoice}
        // {!countryData ? "Choose a Country" : countryData?.country}
        onChange={(e) => {
          getCountryInfo(e.target.value);
        }}
      >
        <option value="Select a country">select a country</option>
        {isLoading ? (
          <></>
        ) : (
          data?.map((item, index) => {
            return (
              <option key={index} value={item.country}>
                {item.country}
              </option>
            );
          })
        )}
      </select>
      {countryData === undefined ? (
        <></>
      ) : (
        <>
          <div>Historical data</div>

          <>
            {cases?.map((item) => {
              return (
                <div key={item[0]}>
                  {item[0]} - {item[1]}
                </div>
              );
            })}
          </>
        </>
      )}
    </Wrapper>
  );
};

const Wrapper = styled.div``;

export default Country;
