import React from "react";
import styled from "styled-components";
const HomePage = () => {
  const [data, setData] = React.useState();

  React.useEffect(() => {
    const getCountriesData = async () => {
      const result = await fetch("https://disease.sh/v3/covid-19/countries");
      const data = await result.json();
      console.log({ data });
      setData(data);
      return data;
    };

    getCountriesData();
  }, []);
  console.log({ data });

  let chosenCountry;
  data?.filter((item) => {
    if (item.country === "Madagascar") {
      return (chosenCountry = item);
    }
    return chosenCountry;
  });

  console.log({ chosenCountry });
  return (
    <Wrapper>
      Homepage
      <div>
        <select value="Choose a Country">
          {data?.map((item) => {
            return (
              <option onChange={(e) => {}} value={"Choose a country"}>
                {item.country}
              </option>
            );
          })}
        </select>
      </div>
      <div>Country data</div>
    </Wrapper>
  );
};

const Wrapper = styled.div``;

export default HomePage;
