import React, { useContext } from "react";
import styled from "styled-components";
import { WorldDataContext } from "./WorldDataContext";
import WorldMap from "./WorldMap";
import Select from "@material-ui/core/Select";
import MenuItem from "@material-ui/core/MenuItem";
import { FormControl, InputLabel } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";

let casesColor;
let deathsColor;
let recoveredColor;

const rounded = (num) => {
  if (num > 1000000000) {
    return Math.round(num / 100000000) / 10 + "Bn";
  } else if (num > 1000000) {
    return Math.round(num / 100000) / 10 + "M";
  } else if (num > 999) {
    return Math.round(num / 100) / 10 + "K";
  } else {
    return num;
  }
};

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 150,
    color: "black",
  },
  CountryLabel: {
    color: "white",
    fontWeight: "bold",
  },
  MenuItem: {
    color: "black",
    marginRight: "5px",
  },
}));

const HomePage = () => {
  const formatedNum = (num) => {
    return new Intl.NumberFormat("en").format(num);
  };

  const classes = useStyles();
  const {
    data,
    country,
    isLoading,
    getSpecificCountry,
    coordinates,
    zoomScale,
    formValue,
  } = useContext(WorldDataContext);

  const todayCases = rounded(country.todayCases);
  const todayDeaths = rounded(country.todayDeaths);
  const todayRecovered = rounded(country.todayRecovered);

  console.log({ todayCases, todayDeaths, todayRecovered });

  return (
    <Wrapper>
      <CountryContainer>
        <FormControl className={classes.formControl}>
          <InputLabel className={classes.CountryLabel}>Country</InputLabel>
          <Select
            className={classes.formControl}
            value={formValue}
            onChange={(e) => {
              getSpecificCountry(e.target.value);
            }}
          >
            {isLoading ? (
              <MenuItem value=""></MenuItem>
            ) : (
              data?.map((item, index) => {
                return (
                  <MenuItem
                    className={classes.MenuItem}
                    key={index}
                    value={item.country}
                  >
                    <Flag
                      style={{ marginRight: "10px" }}
                      src={item.countryInfo.flag}
                      alt="flag"
                    />
                    {item.country}
                  </MenuItem>
                );
              })
            )}
          </Select>
        </FormControl>

        {country ? (
          <CountryWrapper>
            <ImgWrapper>
              <Flag src={country?.countryInfo.flag} />
              <div>{country?.country}</div>
            </ImgWrapper>
            {/* <div>population: {formatedNum(country?.population)}</div> */}
            <InfoBoxes>
              <CountryStats>
                <h3>
                  Total cases :<br></br> {formatedNum(country?.cases)}
                </h3>
                <CasesNum style={todayCases > 0 ? "green" : "black"}>
                  {todayCases}
                </CasesNum>
              </CountryStats>
              <CountryStats>
                <h3>
                  Total Deaths :<br></br> {formatedNum(country?.deaths)}
                </h3>
                <DeathsNum>{todayDeaths}</DeathsNum>
              </CountryStats>
              <CountryStats>
                <h3>
                  Total Recovered :<br></br> {formatedNum(country?.recovered)}
                </h3>
                <RecovNum>{todayRecovered}</RecovNum>
              </CountryStats>
            </InfoBoxes>
          </CountryWrapper>
        ) : (
          <div>👆🏼 Select a country 👆🏼 </div>
        )}
      </CountryContainer>
      <WorldContainer>
        <WorldMap
          data={data}
          country={country}
          coordinates={coordinates}
          zoomScale={zoomScale}
        />
      </WorldContainer>
    </Wrapper>
  );
};

const Wrapper = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: row;
  /* align-items: center; */
`;

const CountryContainer = styled.div`
  padding: 10px 10px;
  height: auto;
  display: flex;
  flex-direction: column;
  /* justify-content: space-evenly; */
`;

const Flag = styled.img`
  border-radius: 50%;
  height: 30px;
  width: 30px;
  margin-right: 10px;
`;

const CountryWrapper = styled.div`
  display: flex;
  flex-direction: column;
  height: 100%;
`;

const ImgWrapper = styled.div`
  display: flex;
  flex-direction: row;
  align-self: center;
  margin-bottom: 20px;
`;
const InfoBoxes = styled.div`
  display: flex;
  flex-direction: column;
  height: 100%;
  justify-content: space-between;
`;

const CountryStats = styled.div`
  border: 2px solid white;
  background-color: white;
  height: 100%;
  border-radius: 8px;
  margin: 10px 0;
  h3 {
    color: black;
  }
  span {
    color: black;
  }
`;
const CasesNum = styled.span`
  color: black;
`;

const DeathsNum = styled.span`
  color: black;
`;

const RecovNum = styled.span`
  color: black;
`;

const WorldContainer = styled.div`
  margin: 0 auto;
  width: 100%;
  height: 100%;
`;

export default HomePage;
