import React, { useContext, useState } from "react";
import styled from "styled-components";
import { useParams } from "react-router-dom";

const Country = () => {
  const [countryData, setCountryData] = useState();
  const [countryParams, setCountryParams] = useState({});
  // const { country, days } = useParams();
  const { data, country, isLoading, getSpecificCountry } = useContext(
    WorldDataContext
  );

  React.useEffect(() => {
    const getCountryInfo = async (specificCountry, numDays) => {
      const response = await fetch(`/api/${specificCountry}/${numDays}`);
      const result = await response.json();
      return result;
    };

    getCountryInfo(country, days).then((data) => {
      setCountryData(data.countryInfo);
    });
  }, [countryParams]);

  const cases = countryData && Object.entries(countryData?.timeline?.cases);
  console.log({ cases });
  return (
    <Wrapper>
      {countryData === undefined ? (
        <></>
      ) : (
        <>
          <div>Historical data</div>
          <h3>{countryData.country}</h3>
          <div>Choose a country</div>
          <select
            onChange={(e) => {
              getSpecificCountry(e.target.value);
            }}
          >
            <option></option>
          </select>
          <>
            {cases?.map((item) => {
              return (
                <div key={item[0]}>
                  {item[0]} - {item[1]}
                </div>
              );
            })}
          </>
        </>
      )}
    </Wrapper>
  );
};

const Wrapper = styled.div``;

export default Country;
