import React, { useContext } from "react";
import {
  AreaChart,
  Area,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
} from "recharts";
import { WorldDataContext } from "./WorldDataContext";
import styled from "styled-components";

// const formatedNum = (num) => {
//   return new Intl.NumberFormat("en").format(num);
// };

const UserChart = () => {
  const { countryData } = useContext(WorldDataContext);

  let numCases;
  let numDeaths;
  let numRecovered;
  let dates;

  if (!countryData) {
    return null;
  } else {
    numCases = Object.values(countryData?.cases);
    numDeaths = Object.values(countryData?.deaths);
    numRecovered = Object.values(countryData?.recovered);
    dates = Object.keys(countryData?.cases);
  }

  const covData = [];
  for (let i = 0; i < numCases?.length; i++) {
    let d = {
      date: dates[i],
      cases: numCases[i],
      deaths: numDeaths[i],
      recovered: numRecovered[i],
    };
    covData.push(d);
  }

  return (
    <>
      {!countryData ? (
        <></>
      ) : (
        <ChartContainer>
          <AreaChart
            width={600}
            height={400}
            data={covData}
            margin={{ top: 10, right: 0, left: 0, bottom: 0 }}
            padding={"10px"}
          >
            <CartesianGrid strokeDasharray="4 4" />
            <XAxis dataKey="date" />
            <YAxis />
            <Tooltip contentStyle={{ backgroundColor: "black" }} />
            <Area
              type="monotone"
              dataKey="cases"
              stackId="cases"
              stroke="#dfc118"
              fill="#d8cd30"
            />
            <Area
              type="monotone"
              dataKey="deaths"
              stackId="deaths"
              stroke="#ff0000"
              fill="#f50303"
            />
            <Area
              type="monotone"
              dataKey="recovered"
              stackId="recovered"
              stroke="#6bff58"
              fill="#49da25"
            />
          </AreaChart>
        </ChartContainer>
      )}
    </>
  );
};

const ChartContainer = styled.div``;

export default UserChart;
