import React from "react";
import styled from "styled-components";
import { useParams } from "react-router-dom";

const Country = () => {
  const [countryData, setCountryData] = React.useState();

  const { country, days } = useParams();
  // console.log({ country, days });

  React.useEffect(() => {
    const getCountryInfo = async (specificCountry, numDays) => {
      const response = await fetch(`/api/${specificCountry}/${numDays}`);
      const result = await response.json();

      return result;
    };

    getCountryInfo(country, days).then((data) => {
      setCountryData(data.countryInfo);
      console.log(data);
    });
  }, []);

  // console.log("data: ", countryData);

  return (
    <Wrapper>
      {countryData === undefined ? (
        <></>
      ) : (
        <>
          <div>Country data page</div>
          <div>{countryData.country}</div>
        </>
      )}
    </Wrapper>
  );
};

const Wrapper = styled.div``;

export default Country;
