import React, { useState, useEffect, useContext } from "react";
import styled from "styled-components";

import {
  MapContainer,
  Marker,
  Popup,
  TileLayer,
  useMap,
  useMapEvent,
} from "react-leaflet";
import { WorldDataContext } from "./WorldDataContext";

function GoToPosition() {
  const { coordinates, zoomScale } = useContext(WorldDataContext);

  const map = useMapEvent("click", () => {
    map.flyTo(coordinates, zoomScale);
  });
  //   console.log("map center:", map.getCenter());
  return null;
}

const WorldMap = ({ country, coordinates, zoomScale }) => {
  //   const [coordinates, setCoordinates] = useState([0, 0]);
  //   const [zoomScale, setZoomScale] = useState(2);
  const currentPosition = [45.508888, -73.561668];
  const defaultScaling = 5;
  //   const map = useMap( );
  //   map.setView(coordinates, zoomScale);

  console.log({ country });

  //   useEffect(() => {

  //      setCoordinates([country.countryInfo.lat, country.countryInfo.long]);
  //     setZoomScale(10);
  //   }, [country]);

  console.log({ coordinates, zoomScale });

  return (
    <>
      <div>WorldMap</div>
      <Mapping>
        <MapContainer
          style={{ height: "100%" }}
          center={currentPosition}
          zoom={defaultScaling}
          scrollWheelZoom={false}
        >
          <GoToPosition />
          <TileLayer
            attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          <Marker position={currentPosition}>
            <Popup>This is Home.</Popup>
          </Marker>
        </MapContainer>
      </Mapping>
    </>
  );
};

const Mapping = styled.div`
  height: 600px;
  padding: 10px;
  border: 1px solid grey;
  border-radius: 5px;
`;

export default WorldMap;
