import React from "react";
import styled from "styled-components";
const HomePage = () => {
  const [data, setData] = React.useState();
  const [country, setCountry] = React.useState([]);

  React.useEffect(() => {
    const getCountriesData = async () => {
      const result = await fetch("https://disease.sh/v3/covid-19/countries");
      const data = await result.json();
      // console.log({ data });
      setData(data);
      return data;
    };

    getCountriesData();
  }, [country]);

  const getSpecificCountry = (specificCountry) => {
    const chosenCountry = data?.filter((item) => {
      if (item.country.toLowerCase() === specificCountry.toLowerCase()) {
        return item.country;
      }
    });

    setCountry(chosenCountry);
    return chosenCountry;
  };

  console.log(country);

  return (
    <Wrapper>
      Homepage
      <div>
        <select
          onChange={(e) => {
            getSpecificCountry(e.target.value);
          }}
        >
          <option>Choose a country</option>
          {data?.map((item, index) => {
            return (
              <option key={index} value={item.country}>
                {item.country}
              </option>
            );
          })}
        </select>
      </div>
      <div>Country data</div>
      {country !== undefined ? (
        <>
          <div>population: {country.population}</div>
          <div>cases : {country?.cases}</div>
          <div>Deaths : {country?.deaths}</div>
        </>
      ) : (
        <></>
      )}
    </Wrapper>
  );
};

const Wrapper = styled.div``;

export default HomePage;
