import { Circle, Popup, Tooltip } from "react-leaflet";
import { numeral } from "numeral";

export const displayCountryNum = (data, casesType = "cases") => {
  console.log({ data });
  return data.map((country, index) => {
    console.log(country.cases);
    // let formatNum;
    // if (country.cases > 1000) {
    //   formatNum = "0 a";
    // } else if (country.cases > 1000000) {
    //   formatNum = "0.0a";
    // }
    const numCases = new Intl.NumberFormat("en", {
      notation: "long",
    }).format(country.cases);

    return (
      <Circle
        key={index}
        center={[country.countryInfo.lat, country.countryInfo.long]}
        radius={Math.floor((country.cases * 10) / 100)}
        color={"red"}
        fillColor={"red"}
      >
        <Tooltip>{numCases}</Tooltip>
      </Circle>
    );
  });
};
