import React from "react";
import styled from "styled-components";
const HomePage = () => {
  const [data, setData] = React.useState();
  const [country, setCountry] = React.useState();

  React.useEffect(() => {
    const getCountriesData = async () => {
      const result = await fetch("https://disease.sh/v3/covid-19/countries");
      const data = await result.json();
      console.log({ data });
      setData(data);
      return data;
    };

    getCountriesData();
  }, []);
  console.log({ data });

  const getSpecificCountry = (specificCountry) => {
    let chosenCountry;
    data?.filter((item) => {
      if (item.country.toLowerCase() === specificCountry.toLowerCase()) {
        return (chosenCountry = item);
      }
      return chosenCountry;
    });
    console.log({ chosenCountry });
    setCountry(chosenCountry);
  };

  console.log({ country });
  return (
    <Wrapper>
      Homepage
      <div>
        <select value="Choose a Country">
          {data?.map((item) => {
            return (
              <option
                onSelect={(e) => {
                  getSpecificCountry(e.target.value);
                }}
              >
                {item.country}
              </option>
            );
          })}
        </select>
      </div>
      <div>Country data</div>
      <div>{country}</div>
    </Wrapper>
  );
};

const Wrapper = styled.div``;

export default HomePage;
