import React, { useContext, useEffect, useState } from "react";

import {
  AreaChart,
  Area,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
} from "recharts";
import { WorldDataContext } from "./WorldDataContext";
import { daysCount } from "../helpers/userPageHelper";
const data = [
  { name: "Page A", uv: 4000, pv: 2400, amt: 2400 },
  { name: "Page B", uv: 3000, pv: 1398, amt: 2210 },
  { name: "Page C", uv: 2000, pv: 9800, amt: 2290 },
  { name: "Page D", uv: 2780, pv: 3908, amt: 2000 },
  { name: "Page E", uv: 1890, pv: 4800, amt: 2181 },
  { name: "Page F", uv: 2390, pv: 3800, amt: 2500 },
  { name: "Page G", uv: 3490, pv: 4300, amt: 2100 },
];

const UserChart = () => {
  const { countryData } = useContext(WorldDataContext);
  const [numCases, setCases] = useState(null);
  const [numDeaths, setDeaths] = useState(null);
  const [numRecovered, setRecovered] = useState(null);
  const [dates, setDates] = useState(null);
  console.log({ countryData });

  useEffect(() => {
    if (countryData !== undefined) {
      setCases(Object.values(countryData.cases));
      setDeaths(Object.values(countryData.deaths));
      setRecovered(Object.values(countryData.recovered));
      setDates(Object.keys(countryData.cases));
    }
  }, []);

  // const cases = countryData && Object.values(countryData.cases);
  // const deaths = countryData && Object.values(countryData.deaths);
  // const recovered = countryData && Object.values(countryData.recovered);
  // const covDates = countryData && Object.keys(countryData.cases);
  // const merged = [...cases, ...deaths, ...recovered];

  // const numdays = daysCount();
  // console.log({ cases });
  // console.log({ merged });
  // console.log("CASES: ", merged.slice(0, numdays));
  // console.log("DEATHS: ", merged.slice(numdays, numdays * 2));
  // console.log("RECOVERED: ", merged.slice(numdays * 2, merged.length));
  // console.log({ covDates });

  const covData = [];
  for (let i = 0; i < numCases?.length; i++) {
    let d = {
      date: dates[i],
      cases: numCases[i],
      deaths: numDeaths[i],
      recovered: numRecovered[i],
    };
    covData.push(d);
  }

  return (
    <>
      {!countryData ? (
        <>...Loading</>
      ) : (
        <AreaChart
          width={600}
          height={400}
          data={covData}
          margin={{ top: 10, right: 0, left: 0, bottom: 0 }}
        >
          <CartesianGrid strokeDasharray="4 4" />
          <XAxis dataKey="date" />
          <YAxis />
          <Tooltip contentStyle={{ backgroundColor: "black" }} />
          <Area
            type="monotone"
            dataKey="cases"
            stackId="3"
            stroke="#dfc118"
            fill="#d8cd30"
          />
          <Area
            type="monotone"
            dataKey="deaths"
            stackId="1"
            stroke="#ff0000"
            fill="#f50303"
          />
          <Area
            type="monotone"
            dataKey="recovered"
            stackId="4"
            stroke="#6bff58"
            fill="#49da25"
          />
        </AreaChart>
      )}
    </>
  );
};

export default UserChart;
