import React, { useContext } from "react";
import styled from "styled-components";
import { WorldDataContext } from "./WorldDataContext";
import WorldMap from "./WorldMap";
import Select from "@material-ui/core/Select";
import MenuItem from "@material-ui/core/MenuItem";
import { FormControl, InputLabel } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 150,
    color: "black",
  },
  CountryLabel: {
    color: "white",
    fontWeight: "bold",
  },
  MenuItem: {
    color: "black",
    marginRight: "5px",
  },
}));

const HomePage = () => {
  const formatedNum = (num) => {
    return new Intl.NumberFormat("en").format(num);
  };

  const classes = useStyles();
  const {
    data,
    country,
    isLoading,
    getSpecificCountry,
    coordinates,
    zoomScale,
    formValue,
  } = useContext(WorldDataContext);

  console.log({ country });

  return (
    <Wrapper>
      <CountryContainer>
        <FormControl className={classes.formControl}>
          <InputLabel className={classes.CountryLabel}>Country</InputLabel>
          <Select
            className={classes.formControl}
            value={formValue}
            style={{ border: "1px solid white", borderRadius: "8px" }}
            onChange={(e) => {
              getSpecificCountry(e.target.value);
            }}
          >
            {isLoading ? (
              <MenuItem value=""></MenuItem>
            ) : (
              data?.map((item, index) => {
                return (
                  <MenuItem
                    className={classes.MenuItem}
                    key={index}
                    value={item.country}
                  >
                    <Flag
                      style={{ marginRight: "10px" }}
                      src={item.countryInfo.flag}
                      alt="flag"
                    />
                    {item.country}
                  </MenuItem>
                );
              })
            )}
          </Select>
        </FormControl>

        {country ? (
          <CountryWrapper>
            <ImgWrapper>
              <Flag src={country?.countryInfo.flag} />
              <div>{country?.country}</div>
            </ImgWrapper>
            {/* <div>population: {formatedNum(country?.population)}</div> */}
            <InfoBoxes>
              <CountryStats>
                <p>Total cases : {formatedNum(country?.cases)}</p>
              </CountryStats>
              <CountryStats>
                <p>Total Deaths : {formatedNum(country?.deaths)}</p>
              </CountryStats>
              <CountryStats>
                <p>Total Recovered : {formatedNum(country?.recovered)}</p>
              </CountryStats>
            </InfoBoxes>
          </CountryWrapper>
        ) : (
          <div>👆🏼 Select a country 👆🏼 </div>
        )}
      </CountryContainer>
      <WorldContainer>
        <WorldMap
          data={data}
          country={country}
          coordinates={coordinates}
          zoomScale={zoomScale}
        />
      </WorldContainer>
    </Wrapper>
  );
};

const Wrapper = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
`;

const CountryContainer = styled.div`
  padding: 10px 0;
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`;

const Flag = styled.img`
  border-radius: 50%;
  height: 30px;
  width: 30px;
  margin-right: 10px;
`;

const CountryWrapper = styled.div`
  display: flex;
  flex-direction: column;
  height: 100%;
`;

const ImgWrapper = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  margin-bottom: 20px;
`;
const InfoBoxes = styled.div`
  display: flex;
  flex-direction: column;
  height: fit-content;
  justify-content: space-between;
`;

const CountryStats = styled.div`
  border: 2px solid white;
  background-color: white;
  height: 100px;
  border-radius: 8px;
  margin: 10px 0;
  p {
    color: black;
  }
`;

const WorldContainer = styled.div`
  margin: 0 auto;
  width: 100%;
  height: 100%;
`;

export default HomePage;
