import React from "react";
import styled from "styled-components";
const HomePage = () => {
  const [data, setData] = React.useState();
  const [country, setCountry] = React.useState();

  React.useEffect(() => {
    const getCountriesData = async () => {
      const result = await fetch("/api/home", {
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
        },
      });
      const data = await result.json();
      console.log({ data });
      return data;
    };

    getCountriesData().then((countriesData) => {
      console.log({ countriesData });
      setData(countriesData);
    });

    // setData(countriesData);
  }, [country]);

  const getSpecificCountry = (specificCountry) => {
    const chosenCountry = data?.find((item) => {
      return item.country.toLowerCase() === specificCountry.toLowerCase();
    });

    setCountry(chosenCountry);
    return chosenCountry;
  };

  console.log({ country });

  return (
    <Wrapper>
      Homepage
      <div>
        <select
          onChange={(e) => {
            getSpecificCountry(e.target.value);
          }}
        >
          <option>Choose a country</option>
          {data?.map((item, index) => {
            return (
              <option key={index} value={item.country}>
                {item.country}
              </option>
            );
          })}
        </select>
      </div>
      <div>Country data</div>
      {country ? (
        <>
          <ImgWrapper>
            <Flag src={country?.countryInfo.flag} />
          </ImgWrapper>
          <div>population: {country?.population}</div>
          <div>cases : {country?.cases}</div>
          <div>Deaths : {country?.deaths}</div>
          <div>Recovered : {country?.recovered}</div>
        </>
      ) : (
        <>Waiting for data</>
      )}
    </Wrapper>
  );
};

const Wrapper = styled.div``;
const ImgWrapper = styled.div``;
const Flag = styled.img`
  border-radius: 50%;
  height: 150px;
  width: 150px;
  border: solid 2px black;
  object-fit: contain;
`;
export default HomePage;
