import React from "react";
import styled from "styled-components";
import { useParams } from "react-router-dom";

const Country = () => {
  const [countryData, setCountryData] = React.useState();

  const { country, days } = useParams();
  console.log({ country, days });

  React.useEffect(() => {
    const getCountryInfo = async (specificCountry, numDays) => {
      const response = await fetch("/api/:country/:days");
      const result = await response.json();

      return result;
    };

    getCountryInfo(country, days).then((countryInfo) => {
      setCountryData(countryInfo);
      console.log(countryInfo);
    });
  }, []);

  console.log("data: ", countryData);

  return (
    <Wrapper>
      <div>Country data page</div>
    </Wrapper>
  );
};

const Wrapper = styled.div``;

export default Country;
