import React from "react";
import styled from "styled-components";
import { useParams } from "react-router-dom";

const Country = () => {
  const [countryData, setCountryData] = React.useState();

  const { country, days } = useParams();
  // console.log({ country, days });

  React.useEffect(() => {
    const getCountryInfo = async (specificCountry, numDays) => {
      const response = await fetch(`/api/${specificCountry}/${numDays}`);
      const result = await response.json();

      return result;
    };

    getCountryInfo(country, days).then((data) => {
      setCountryData(data.countryInfo);
      console.log(data);
    });
  }, [country, days]);

  // console.log("data: ", countryData);
  const cases = Object.entries(countryData?.timeline?.cases) || undefined;
  console.log({ cases });
  return (
    <Wrapper>
      {countryData === undefined ? (
        <></>
      ) : (
        <>
          <div>Country data page</div>
          <h3>{countryData.country}</h3>
          <>
            {cases.map((item) => {
              return (
                <div>
                  {item[0]} - {item[1]}
                </div>
              );
            })}
          </>
        </>
      )}
    </Wrapper>
  );
};

const Wrapper = styled.div``;

export default Country;
