import React, { useContext } from "react";
import {
  AreaChart,
  Area,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
} from "recharts";
import { WorldDataContext } from "./WorldDataContext";
// import { daysCount } from "../helpers/userPageHelper";
// const data = [
//   { name: "Page A", uv: 4000, pv: 2400, amt: 2400 },
//   { name: "Page B", uv: 3000, pv: 1398, amt: 2210 },
//   { name: "Page C", uv: 2000, pv: 9800, amt: 2290 },
//   { name: "Page D", uv: 2780, pv: 3908, amt: 2000 },
//   { name: "Page E", uv: 1890, pv: 4800, amt: 2181 },
//   { name: "Page F", uv: 2390, pv: 3800, amt: 2500 },
//   { name: "Page G", uv: 3490, pv: 4300, amt: 2100 },
// ];

const UserChart = () => {
  const { countryData } = useContext(WorldDataContext);

  const numCases = countryData && Object.values(countryData.cases);
  const numDeaths = countryData && Object.values(countryData.deaths);
  const numRecovered = countryData && Object.values(countryData.recovered);
  const dates = countryData && Object.keys(countryData.cases);

  const covData = [];
  for (let i = 0; i < numCases?.length; i++) {
    let d = {
      date: dates[i],
      cases: numCases[i],
      deaths: numDeaths[i],
      recovered: numRecovered[i],
    };
    covData.push(d);
  }

  return (
    <>
      {!countryData ? (
        <>...Loading</>
      ) : (
        <AreaChart
          width={800}
          height={400}
          data={covData}
          margin={{ top: 10, right: 0, left: 0, bottom: 0 }}
          padding={"10px"}
        >
          <CartesianGrid strokeDasharray="4 4" />
          <XAxis dataKey="date" />
          <YAxis />
          <Tooltip contentStyle={{ backgroundColor: "black" }} />
          <Area
            type="monotone"
            dataKey="cases"
            stackId="3"
            stroke="#dfc118"
            fill="#d8cd30"
          />
          <Area
            type="monotone"
            dataKey="deaths"
            stackId="2"
            stroke="#ff0000"
            fill="#f50303"
          />
          <Area
            type="monotone"
            dataKey="recovered"
            stackId="1"
            stroke="#6bff58"
            fill="#49da25"
          />
        </AreaChart>
      )}
    </>
  );
};

export default UserChart;
