import React, { useContext, useState } from "react";
import styled from "styled-components";
import { WorldDataContext } from "./WorldDataContext";

const Country = () => {
  const {
    data,
    isLoading,
    getCountryInfo,
    countryData,
    countryChoice,
  } = useContext(WorldDataContext);
  const [covNum, setCovNum] = useState([{ cases: 0, recovered: 0, deaths: 0 }]);

  React.useEffect(() => {}, []);

  console.log({ countryData });

  return (
    <Wrapper>
      <div>Choose a country</div>
      <select
        value={countryChoice}
        onChange={(e) => {
          getCountryInfo(e.target.value);
        }}
      >
        <option value={countryChoice}>{countryChoice}</option>
        {isLoading ? (
          <></>
        ) : (
          data?.map((item, index) => {
            return (
              <option key={index} value={item.country}>
                {item.country}
              </option>
            );
          })
        )}
      </select>
      {countryData === undefined ? (
        <></>
      ) : (
        <>
          <div>Historical data</div>

          <>
            {/* {countryData?.timeline ? ( .map((item) => {
              return (
                <div key={item[0]}>
                  {item[0]} - {item[1]}
                </div>
              );
            })} */}
          </>
        </>
      )}
    </Wrapper>
  );
};

const Wrapper = styled.div``;

export default Country;
