import React, { useState } from "react";

import {
  FormControl,
  makeStyles,
  TextField,
  IconButton,
  InputAdornment,
  Button,
} from "@material-ui/core";
import Visibility from "@material-ui/icons/Visibility";
import VisibilityOff from "@material-ui/icons/VisibilityOff";
import clsx from "clsx";
import firebase, { db } from "../firebase";
import { Link } from "react-router-dom";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    flexWrap: "wrap",
    width: "100%",
    alignItems: "center",
    justifyContent: "center",
    "& .MuiFormLabel-root": {
      color: "white",
    },
  },
  form: {
    width: "350px",
    marginTop: theme.spacing(6),
  },
  formControl: {
    width: "100%",
    marginBottom: theme.spacing(3),
  },
  input: {
    color: "white",
  },
}));

export const SignUp = () => {
  const classes = useStyles();
  const [password, setPassword] = useState("");
  const [confPassword, setConfPassword] = useState("");
  const [email, setEmail] = useState("");
  const [userName, setUserName] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [errMsg, setErrMsg] = useState();

  const handleClickShowPassword = () => {
    setShowPassword(!showPassword);
  };
  const handleMouseDownPassword = (e) => {
    e.preventDefault();
  };
  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const userCredentials = await firebase
        .auth()
        .createUserWithEmailAndPassword(email, password);
      console.log(userCredentials);

      await db.collection("users").doc(userCredentials.user.uid).set({
        displayName: userName,
        email: userCredentials.user.email,
        emailVerified: userCredentials.user.emailVerified,
        providerId: userCredentials.user.providerId,
      });

      console.log("ðŸŽŠ firestore doc written successfully ðŸŽŠ");
    } catch (error) {
      setErrMsg(error.message);
      console.log(error.message);
    }
  };

  return (
    <div className={classes.root}>
      <h2 className={classes.root}>SIGN UP</h2>
      <form className={classes.form} onSubmit={handleFormSubmit}>
        <FormControl className={clsx(classes.formControl)}>
          <TextField
            variant="filled"
            label="Username"
            type="text"
            value={userName}
            InputProps={{ className: classes.input }}
            onChange={(e) => setUserName(e.target.value)}
          />
        </FormControl>

        <FormControl className={clsx(classes.formControl)}>
          <TextField
            variant="filled"
            label="Email"
            type="text"
            value={email}
            InputProps={{ className: classes.input }}
            onChange={(e) => setEmail(e.target.value)}
          />
        </FormControl>
        <FormControl className={clsx(classes.formControl)}>
          <TextField
            variant="filled"
            label="Password"
            type={showPassword ? "text" : "password"}
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            InputLabelProps={{ color: "white" }}
            InputProps={{
              className: classes.input,
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton
                    aria-label="toggle password visibility"
                    onClick={handleClickShowPassword}
                    onMouseDown={handleMouseDownPassword}
                  >
                    {showPassword ? <Visibility /> : <VisibilityOff />}
                  </IconButton>
                </InputAdornment>
              ),
            }}
          />
        </FormControl>
        <FormControl className={clsx(classes.formControl)}>
          <TextField
            variant="filled"
            label="Confirm Password"
            type={showPassword ? "text" : "password"}
            value={confPassword}
            onChange={(e) => setConfPassword(e.target.value)}
            InputProps={{
              className: classes.input,
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton
                    aria-label="toggle password visibility"
                    onClick={handleClickShowPassword}
                    onMouseDown={handleMouseDownPassword}
                  >
                    {showPassword ? <Visibility /> : <VisibilityOff />}
                  </IconButton>
                </InputAdornment>
              ),
            }}
          />
        </FormControl>
        {email === "" ||
        userName === "" ||
        password === "" ||
        confPassword === "" ||
        password !== confPassword ? (
          <Button type="submit" variant="contained" color="red" disabled>
            SIGN UP
          </Button>
        ) : (
          <Button type="submit" variant="contained" color="red">
            SIGN UP
          </Button>
        )}
        {errMsg ? <span className={classes.root}>{errMsg}</span> : <></>}
        <div className={classes.root}>
          <Link to="/sign-in">Already have an account ? Sign in here.</Link>
        </div>
      </form>
    </div>
  );
};
